import React, { useState } from "react";
import axios from "axios";

function App() {
  const [photo, setPhoto] = useState("");
  const [result, setResult] = useState([]);
  const [selectedPhotos, setSelectedPhotos] = useState([]);

  const addPhoto = (photos) => {
    axios
      .post("http://localhost:5001/api/photos", photos)
      .then((res) => {
        console.log(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const getPhotos = () => {
    axios
      .get("http://localhost:5001/api/photos")
      .then((res) => {
        console.log(res.data);
        setResult(res.data);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  const changePhoto = () => {
    axios
      .get(
        `https://api.unsplash.com/search/photos?page=1&query=${photo}&client_id=hq9Bei2bKr-GBZXix2eLN02tgl_Aysgrtuwx7SJBhzE`
      )
      .then((response) => {
        const photos = response.data.results.map((photo) => {
          return {
            id: photo.id,
            url: photo.urls.small,
            selected: false,
          };
        });
        setResult(photos);
      });
  };

  React.useEffect(() => {
    getPhotos();
  }, []);

  return (
    <>
      <div className="container text-center my-5">
        <div className="row justify-content-center">
          <div className="col-lg-4 col-md-6 col-8">
            <input
              type="text"
              className="form-control"
              value={photo}
              onChange={(e) => {
                setPhoto(e.target.value);
              }}
            />
          </div>
          <div className="col-lg-1 col-md-2 col-2">
            <button
              type="submit"
              onClick={changePhoto}
              className="btn btn-primary"
            >
              Get Photo
            </button>
          </div>
          <div className="col-lg-3 col-md-4 col-6">
            <button
              type="button"
              className="btn btn-primary"
              disabled={selectedPhotos.length === 0}
              onClick={() => {
                addPhoto(selectedPhotos);
              }}
            >
              Save {selectedPhotos.length} Photos
            </button>
          </div>
        </div>
      </div>

      <div className="container">
        <div className="row text-center text-lg-start">
          {result.map((photo) => {
            return (
              <div className="col-lg-3 col-md-4 col-6 mb-4" key={photo.id}>
                <img
                  className="img-fluid img-thumbnail d-block h-100"
                  src={photo.url}
                  alt=""
                />
                <div className="form-check my-2">
                  <input
                    className="form-check-input"
                    type="checkbox"
                    checked={photo.selected}
                    onChange={() => {
                      const photos = [...result];
                      const index = photos.findIndex((p) => p.id === photo.id);
                      photos[index].selected = !photos[index].selected;
                      setSelectedPhotos(photos.filter((p) => p.selected));
                      setResult(photos);
                    }}
                  />
                  <label className="form-check-label">Select</label>
                </div>
              </div>
            );
          })}
        </div>
        </div>
    </>
  );
}

export default App;
